// C++
#include <bits/stdc++.h>
using namespace std;
void SetIO(string file){
   cin.tie(0); ios_base::sync_with_stdio(0);
   freopen((file + ".txt").c_str(), "r", stdin); // cin now reads from file
   freopen((file + ".txt").c_str(), "w", stdout); // cout now writes to file
}

int main() {
	SetIO();
}

