// C++
#include <bits/stdc++.h>
using namespace std;

class authentication{
   private:
      string password;
   public:
      bool authenticate(string input){
         return (input == password);
      }
};

int main(){
   auth = authentication();
   string test_password; cin >> test_password;
   cout << auth.authenticate(test_password); // Can access public methods
   cout << auth.password; // CANNOT access private methods, will throw exception
   return 0;
}
